import React, { Component } from 'react';

class Counter extends Component {
    constructor(props) {
      super(props);
      this.increment = this.increment.bind(this)
      this.state = {
        status: "stopped",
        lap: 0,
        color: "#00B1E1",
      };
    }

    increment() {
        this.setState({
          count: this.state.lap + 1
        });
        this.toggleColor();
    }

    toggleColor() {
        if(this.state.color == "#00B1E1") {
            this.setState({
                color: "#E9573F"
            });
        } else {
            this.setState({
                color: "#00B1E1"
            });
        }
        this.setState({
          if: this.state.lap + 1
        });


    }
    startTimer = () => {
        this.setState({
          status: "running",
        });
        this.timer = setInterval( () => {
            this.setState({ lap: this.lap + 1 });
        });
    }

    render() {
        return(
        <div style={{backgroundColor: this.state.color}} onClick={this.increment}>{this.state.lap}
        <button onClick={this.startTimer}>Start</button>
        <button onClick={this.increment}>Pause</button>
        <button onClick={this.increment}>Reset</button>
        </div>
        )
    }
}

  export default Counter;
